fn elevator(input: &str) -> i32 {
    // returns floor at which the santa should exit the elevator
    let mut floor = 0;
    for instruction in input.chars() {
        match instruction {
            instruction if instruction == '(' => floor += 1,
            instruction if instruction == ')' => floor -= 1,
            _ => (),           
        };
    }
    floor
}

fn basement_at_which_step(input: &str) -> Option<usize> {
    let mut floor: i32 = 0;
    for (index, instruction) in input.chars().enumerate() {
        match instruction {
            instruction if instruction == '(' => floor += 1,
            instruction if instruction == ')' => floor -= 1,
            _ => (),  
        };
        if floor == -1 { return Some(index + 1);}
    }
    None
}
#[cfg(test)]
mod tests {
    use super::*;
    #[test]
    fn one_floor_up() {
        assert_eq!(elevator("("), 1);
    }
    #[test]
    fn one_floor_down() {
        assert_eq!(elevator(")"), -1);
    }
    #[test]
    fn one_up_and_one_down() {
        assert_eq!(elevator("()"), 0);
    }
    #[test]
    fn one_down_and_one_up() {
        assert_eq!(elevator(")("), 0);
    }
    #[test]
    fn stay_on_ground() {
        assert_eq!(elevator(""), 0);
    }
    #[test]
    fn basement_at_step_one() {
        assert_eq!(basement_at_which_step(")"), Some(1));
    }
    #[test]
    fn never_reach_basement() {
        assert_eq!(basement_at_which_step("(()"), None);
    }
    #[test]
    fn reach_basement_in_3rd_step() {
        assert_eq!(basement_at_which_step("())"), Some(3));
    }

}
